import { default as template } from './book-preview.hbs';
import Utils from '../../utils';
import Cart from '../cart/cart';

export default class BookPreview {
    constructor ( { isbn, cover, excerpt, price, synopsis, title } ) {
        this.data = { isbn, cover, excerpt, price, synopsis, title };
        this.el = null;
        this.addButton = null;
        this.cartLoader = null;
        this.addCartListener = this.onAdd.bind( this );
        this.seeCartListener = this.onSeeCart.bind( this );
    }

    prepare () {
        this.el = document.createElement( 'div' );
        this.el.innerHTML = template(
            Object.assign( {},
                this.data, {
                    price: Utils.toLocalCurrency( this.data.price )
                } )
        );
        this.cartLoader = this.el.querySelector( '.cart-loader' );
        this.addButton = this.el.querySelector( '.cta-add-cart' );
        this.addButton.addEventListener( 'click', this.addCartListener );
        this.toggleLoaderCartButton( 'remove' );

        this.toastButton = $( '<button class="btn toast-action waves-effect grey lighten-5 black-text toast-see-cart">See your cart</button>' );
        this.toastButton[ 0 ].addEventListener( 'click', this.seeCartListener );
        this.$toastContent = $( '<span>Your book has been added to your cart</span>' ).add( this.toastButton );

        return this;
    }

    async onAdd ( e ) {
        e.preventDefault();

        this.toggleLoaderCartButton( 'add' );

        this.cart = new Cart(); // this is a singleton so don't panic!
        await this.cart.addBook( {
            isbn: this.data.isbn,
            price: this.data.price,
            title: this.data.title
        } );

        this.toggleLoaderCartButton( 'remove' );
        Materialize.toast( this.$toastContent, 30000, 'toast-feedback-add green' );
    }

    /**
     * Show or hide add to cart button and loader
     * @param action:string must be 'add' or 'remove'
     */
    toggleLoaderCartButton ( action ) {
        this.cartLoader.classList[ action ]( 'active' );
        this.addButton.classList[ action ]( 'hide' );
    }

    onSeeCart () {
        Materialize.Toast.removeAll();
        this.cart.onOpenCart();
    }

    destroy () {
        this.addButton.removeEventListener( 'click', this.addCartListener );
        this.toastButton[ 0 ].removeEventListener( 'click', this.seeCartListener );
    }
}

import { default as template } from './bookslist.hbs';
import ApiPotier from '../../services/api-potier';
import BookPreview from '../book-preview/book-preview';

export default class BooksList {
    constructor ( el ) {
        this.el = el;
        this.data = null;
        this.content = null;
        this.previews = null;
        this.api = new ApiPotier();
    }

    async init () {
        // Wait for the all books data from api
        return new Promise( ( resolve, reject ) => {
            this.api.getAllBooks()
                .then( async ( data ) => {
                    this.data = data;
                    resolve();
                } )
                .catch( reject );
        } );
    }

    prepare () {
        // Booklisting Template
        this.content = document.createElement( 'div' );
        this.content.innerHTML = template( { books: this.data } );

        // Create BookPreview instances
        this.previews = this.data.map( data =>
            new BookPreview( {
                isbn: data.isbn,
                cover: data.cover,
                price: data.price,
                synopsis: data.synopsis,
                title: data.title,
                excerpt: data.excerpt
            } )
                .prepare()

        );
        return this;
    }

    display () {
        // display booklisting template
        this.el.appendChild( this.content.firstChild );

        // display children templates: instances of BookPreview
        this.previews.forEach( book => {
            this.el.querySelector( '.booklisting' ).appendChild( book.el.firstChild );
        } );
        return this;
    }

    destroy () {
        this.el.remove();
    }
}

import { default as template } from './cart.hbs';
import Layout from '../../layout';
import Utils from '../../utils';
import ApiPotier from '../../services/api-potier';
import Lockr from 'lockr';

let instance = null;

export default class Cart {
    constructor () {
        if ( !instance ) {
            instance = this;
        }

        this.el = null;
        this.content = null;
        this.$modal = null;
        this.layout = null;
        this.api = new ApiPotier();

        return instance;
    }

    async init ( el ) {
        this.el = el;
        this.items = [];
        this.discount = null;
        this.subTotal = null;
        this.totalOrder = null;
        this.offers = null;

        this.content = document.createElement( 'div' );

        // Listen to layout for click on cart menu item
        this.layout = new Layout();
        this.layout.on( 'open-cart', this.onOpenCart.bind( this ) );

        // Retrieve items if existing from the localStorage
        Lockr.prefix = 'xbks';
        const localStorageItems = Lockr.get( 'cart-items' );
        if ( localStorageItems ) {
            this.items = localStorageItems;
            await this.updateTotals();
        }

        this.render();
    }

    initModal () {
        this.$modal = $( '.modal-cart' );
        this.$modal.modal( {
            opacity: 0.9, // Opacity of modal background
            inDuration: 200, // Transition in duration
            outDuration: 200, // Transition out duration
            // Callback for Modal open. Modal and trigger parameters available.
            // ready: this.onModalOpen.bind( this ),
            // Callback for Modal close
            // complete: this.onModalClose.bind( this )
        } );
    }

    onOpenCart () {
        this.$modal.modal( 'open' );
    }

    // onModalOpen () {
    //     console.log( 'onModalOpen' );
    // }
    //
    // onModalClose () {
    //     console.log( 'onModalClose' );
    // }

    async addBook ( { isbn, title, price } ) {
        return new Promise( async ( resolve ) => {
            // this.layout.displayMainLoader();

            // Updates books list
            const index = this.items.findIndex( item => item.isbn === isbn );
            // if item already exists in the cart, just increment the quantity
            if ( index >= 0 ) {
                this.items[ index ].quantity++;
            }
            else {
                this.items.push( {
                    isbn: isbn,
                    title: title,
                    price: price,
                    quantity: 1
                } );
            }

            // Save items into the localStorage
            Lockr.set( 'cart-items', this.items );

            await this.updateTotals();

            await this.render();

            // this.layout.removeMainLoader();
            resolve();
        } );
    }

    async updateTotals () {
        console.log( 'updateTotals' );
        // Update total without reduction
        await this.updateSubTotal();

        // Update discount object
        await this.updateDiscount();

        // Update total with applied reduction
        this.totalOrder = this.subTotal - this.discount.amount;
    }

    async updateDiscount () {
        console.log( 'updateDiscount' );
        await this.findOffers();
        this.discount = this.findBestReduction();
    }

    async findOffers () {
        console.log( 'findOffers' );
        // Get list of all isbn
        // ( if there is more than one time, we must add its isbn more than one time)
        const isbnList = [];
        this.items.forEach( book => {
            for ( let i = 0; i < book.quantity; i++ ) {
                isbnList.push( book.isbn );
            }
        } );

        await this.api.getOffers( isbnList )
            .then( data => { this.offers = data; } )
            .catch( error => { console.error( error ); } );
    }

    findBestReduction () {
        const discounts = [];
        this.offers.forEach( offer => {
            discounts.push( offer );
            switch ( offer.type ) {
            case 'percentage':
                discounts[ discounts.length - 1 ].amount = offer.value / 100 * this.subTotal;
                break;
            case 'minus':
                discounts[ discounts.length - 1 ].amount = offer.value;
                break;
            case 'slice':
                discounts[ discounts.length - 1 ].amount = Math.floor( this.subTotal / offer.sliceValue ) * offer.value;
                break;
            default:
                break;
            }
        } );
        // we need to compare amount and keep all the other stuff
        return discounts.reduce( ( maxOffer, offer ) => offer.amount > maxOffer.amount ? offer : maxOffer, discounts[0] );
    }

    updateSubTotal () {
        console.log( 'updateSubTotal' );
        if ( this.items.length > 0 ) {
            // update total without reduction (subtotal)
            const reducedSum = this.items.reduce( ( acc, item ) => {
                const price1 = ( acc.price * ( acc.quantity || 1 ) );
                const price2 = ( item.price * ( item.quantity || 1 ) );
                return ( { price: price1 + price2 } );
            } );
            // if there is only one item in the cart, this is the original item
            this.subTotal = reducedSum.price * ( reducedSum.quantity || 1 );
        }
        else {
            this.subTotal = 0;
            this.totalOrder = 0;
        }
    }

    render () {
        this.content.innerHTML = template( {
            items: this.items.map( item => {
                return Object.assign( {}, item, {
                    total: Utils.toLocalCurrency( item.price * item.quantity ),
                    price: Utils.toLocalCurrency( item.price )
                } );
            } ),
            discount: Object.assign( {}, this.discount, { amount: this.discount ? Utils.toLocalCurrency( this.discount.amount ) : null } ),
            subTotal: this.subTotal !== null ? Utils.toLocalCurrency( this.subTotal ) : 'jjj',
            totalOrder: this.totalOrder !== null ? Utils.toLocalCurrency( this.totalOrder ) : 0
        } );

        this.el.innerHTML = this.content.innerHTML;
        this.initModal();
    }
}

import EventEmitter from 'events';

export default class Layout extends EventEmitter {
    constructor () {
        super();
        if ( !Layout.instance ) {
            Layout.instance = this.init();
        }
        return Layout.instance;
    }

    init () {
        this.loader = null;
        this.createMainLoader();
        this.initMenu();
        return this;
    }

    createMainLoader () {
        this.loader = document.createElement( 'div' );
        this.loader.setAttribute( 'class', 'main-loader progress' );
        const contentLoader = document.createElement( 'div' );
        contentLoader.setAttribute( 'class', 'indeterminate' );
        this.loader.appendChild( contentLoader );
    }

    displayMainLoader () {
        document.body.prepend( this.loader );
    }

    removeMainLoader () {
        this.loader.remove();
    }

    initMenu () {
        // Initialize collapse button
        $( '.button-collapse' ).sideNav( {
            menuWidth: 300,
            edge: 'left',
            closeOnClick: true,
            draggable: true
            // onOpen: $el => { console.log( 'Menu is open', $el[0] ) },
            // onClose: $el => { console.log( 'Menu is close', $el[0] ) },
        } );
        // Initialize collapsible (uncomment the line below if you use the dropdown variation)
        // $('.collapsible').collapsible();

        // Clicks on items
        this.buttonsListener = this.onMenuItem.bind( this );
        this.menuButtons = document.querySelectorAll( '.nav-wrapper .btn-js' );
        this.listenMenu();
    }

    listenMenu () {
        this.menuButtons.forEach( btn => btn.addEventListener( 'click', this.buttonsListener ) );
    }

    onMenuItem ( e ) {
        const role = e.currentTarget.getAttribute( 'data-role' );
        if ( role === 'cart' ) {
            this.emit( 'open-cart' );
            e.preventDefault();
        }
    }
}

