import Layout from '../layout';
import BooksList from '../components/books-list/books-list';
import Cart from '../components/cart/cart';

export default class Home {
    constructor ( el ) {
        console.log( 'Hello Home' );
        this.el = el;
        this.layout = null;
        this.books = null;
        this.bookList = null;
        this.cart = null;

        try {
            this.showPage();
            this.runApp();
        }
        catch ( e ) {
            console.error( `Something's wrong :( ${ e }` );
        }
    }

    showPage () {
        if ( !this.el ) {
            throw new Error( 'This app must be wrapped in a dom element with a ".main-container" class!' );
        }
        else {
            this.el.classList.add( 'complete' );
        }
    }

    async runApp () {
        // Menu and all layout stuff with Materialize
        this.layout = new Layout();

        // Display a loader while waiting for the api request
        this.layout.displayMainLoader();

        // Create a Cart component
        // Cart class is a singleton, so init instruction must be called only here !
        this.cart = new Cart();
        this.cart.init( document.querySelector( '#cart-container' ) );

        // Create a BooksList component
        this.bookList = new BooksList( this.el.querySelector( '#books-list' ) );

        // Wait for the initialization of the BooksList component
        await this.bookList.init()
            .then( () => {
                // when the BooksList component is ready: display it
                this.bookList
                    .prepare()
                    .display();
            } )
            .catch( err => { console.error( err ); } );

        this.layout.removeMainLoader();
    }
}

import 'jquery'; // As Materialize-css requires jQuery, I use it also for http request

let instance = null;

class ApiPotier {
    constructor () {
        if ( !instance ) {
            instance = this;
        }
        this.urlAllBooks = 'http://henri-potier.xebia.fr/books';
        return instance;
    }

    getAllBooks () {
        return new Promise( async ( resolve, reject ) => {
            // TODO: remove timeout, here just to test promise and loader display
            // setTimeout( async () => {
            await $.ajax( {
                url: this.urlAllBooks,
                type: 'GET',
                success: data => {
                    // format data to return exactly what models are waiting for
                    const formattedData = data.map( book => {
                        book.excerpt = book.synopsis && book.synopsis.length > 0 ? book.synopsis[0] : null;
                        return book;
                    } );
                    resolve( formattedData );
                },
                error: reject
            } );
            // }, 3000 );
        } );
    }

    getOffers ( isbnList ) {
        const url = `http://henri-potier.xebia.fr/books/${ isbnList.join( ',' ) }/commercialOffers`;
        return new Promise( async ( resolve, reject ) => {
            // setTimeout( async () => {
            await $.ajax( {
                url: url,
                type: 'GET',
                success: data => { resolve( data.offers ); },
                error: reject
            } );
            // }, 2000 );
        } );
    }
}

export default ApiPotier;

HTMLDocument.prototype.ready = () => new Promise( resolve => document.readyState === 'complete' ? resolve() : new Promise( resolve => ( document.onreadystatechange = () => document.readyState === 'complete' && resolve() ) ).then( resolve() ) );

/**
 * Utility Functions
 * @type Object
 */
const Utils = {
    /**
     * Returns an array of requested length
     * with data or null object in each cell
     * @param length
     * @param data
     * @returns {*}
     */
    getMap: ( length, data = null ) => {
        if ( !length ) return [];
        return Array.from( { length: length }, () => data );
    },

    /**
     * Format in USD currency
     * @param value
     * @returns {string}
     */
    toLocalCurrency: ( value ) => {
        return value.toLocaleString( 'en-US', {
            style: 'currency',
            currency: 'USD',
        } );
    }
};

export default Utils;
